public static List<String> splitStringByNumber(String text, int size) {
    List<String> strings = new ArrayList<String>();
    int index = 0;
    while (index < text.length()) {
        strings.add(text.substring(index, Math.min(index + size,text.length())));
        index += size;
    }
    return strings;
}

